$(function(){
  let scene, camera, renderer;                        // 镜头、镜头、渲染
  let width2 = document.documentElement.clientWidth;
  let height2 = document.documentElement.clientHeight
  const [width, height] = [width2, height2];                 //画面尺寸
  let flag;                                       //旗
  let flagColor = "#fff";                      //旗的颜色
  let flagTexture = null;                         //旗的纹理
  let [sizeW, sizeH, segW, segH]=[40, 30, 40, 30]
  let geometryx=-20
  let geometryy=-35
 let geometrywidth
  if(width2>=1440){
     [sizeW, sizeH, segW, segH] = [40, 30, 40, 30];  //旗的大小，分割数
  }else if(width2<1440&&width2>800){
     [sizeW, sizeH, segW, segH]=[30,20,30,20]
     geometryx=-15
     geometryy=-40
  }else if(width2<=800){
    [sizeW, sizeH, segW, segH]=[17,12,17,12]
    geometryx=-9
    geometryy=-44
  }

  const init = () => {
    //创建幻灯片
    scene = new THREE.Scene();
    //相机的制作（视角60、近距离剪辑1、传真剪辑1000）
    camera = new THREE.PerspectiveCamera(60, width / height, 1, 1000);
    //相机的初始位置
    camera.position.set(0, 0, 40);
    //照相机朝向原点
    camera.lookAt(new THREE.Vector3(0, 0.0));
    //创建渲染器
    renderer = new THREE.WebGLRenderer({ antialias: true });
    renderer.setSize(width, height);
    renderer.setClearColor("#f5f6f7")
    //图像显示区域
    document.getElementById("renderArea").appendChild(renderer.domElement);
    //灯光的制作
    //光源为白色
    const light = new THREE.DirectionalLight("#FFFFFF");
    light.position.set(10, 50, 100);
    scene.add(light);
    //场景光的颜色是灰色的
    const ambientLight = new THREE.AmbientLight("#999999");
    scene.add(ambientLight);
    //页面创建
    //创建柱
    let geometry = new THREE.CylinderGeometry(0.5, 0.5, 100, 16, 1);
    let material = new THREE.MeshPhongMaterial({
      //柱子的颜色是明亮的橙色
      color: "#ffcc99",
      //柱子的光和灰色
      specular: "#999999",
      shininess: 10
    });
    const pole = new THREE.Mesh(geometry, material);
    //波尔的位置
    pole.position.set(geometryx, geometryy, 0);
    scene.add(pole);
    //旗帜的制作
    geometry = new THREE.PlaneGeometry(sizeW, sizeH, segW, segH);
    //创建平面
    material = new THREE.MeshLambertMaterial({
      color: flagColor,
      side: THREE.DoubleSide
    });
    flag = new THREE.Mesh(geometry, material);
    scene.add(flag);
    //水平方向、垂直方向、振幅、速度的初始化
    document.getElementById("h").value = 0.5;
    document.getElementById("v").value = 0.4;
    document.getElementById("w").value = 0.2;
    document.getElementById("s").value = 0.4;
    //相机位置的初始化
    document.getElementById("angle").value = -10;

    setCamera()
    //动画开始
    update();
    loadTexture();

  }

  const initFlag = () => {
    //白旗
    flagColor = "#ffffff";
    document.getElementById("color").value = flagColor;
    flagTexture = null;
    document.getElementById("texture").value = "13";
    setMaterial();
    //相机位置的初始化
    document.getElementById("angle").value = -13;
    console.log(document.getElementById("angle").value)
    setCamera();
    //水平方向、垂直方向、振幅、速度的初始化
    document.getElementById("h").value = 0.7;
    document.getElementById("v").value = 0.6;
    document.getElementById("w").value = 0.2;
    document.getElementById("s").value = 0.4;
  }

  const setColor = () => {
    //将旗帜的色彩演绎得淋漓尽致
    flagColor = document.getElementById("color").value;
    setMaterial();
  }

  const setMaterial = () => {
    //将旗帜的素材再现
    console.log(flagColor);
    flag.material = new THREE.MeshLambertMaterial({
      color: flagColor,
      map: flagTexture,
      side: THREE.DoubleSide
    });
  }

  const loadTexture = files => {
    //图像读取
    console.log(1)
    let textureLoader = new THREE.TextureLoader();
    let url = "data:image/jpeg;base64,";
    const loader = new THREE.TextureLoader();
    loader.load(url, texture => {
      texture.magFilter = THREE.LinearFilter;
      texture.minFilter = THREE.LinearFilter;
      flagTexture = texture;
      setMaterial();
    });

  }

  const setCamera = () => {
    //将旗帜的素材再现

    const angle = document.getElementById("angle").value;
    document.getElementById("textAngle").innerText = `[${angle}°]`;
    camera.position.x = 40 * Math.sin(angle * Math.PI / 180);
    camera.position.z = 40 * Math.cos(angle * Math.PI / 180);
    camera.lookAt(new THREE.Vector3(0, 0, 0));
  }

  const update = () => {
    const h = document.getElementById("h").value;
    const v = document.getElementById("v").value;
    const w = document.getElementById("w").value;
    const s = document.getElementById("s").value;

    for (let y = 0; y < segH + 1; y++) {
      for (let x = 0; x < segW + 1; x++) {
        const index = x + y * (segW + 1);
        // console.log(x);
        // console.log(y);
        // console.log(segW);
        // console.log(index);
        // console.log(flag.geometry.vertices);
        const vertex = flag.geometry.vertices[index];
        const time = Date.now() * s / 50;
        vertex.z = Math.sin(h * x + v * y - time) * w * x / 4;
      }
    }
    flag.geometry.verticesNeedUpdate = true;
    renderer.render(scene, camera);
    window.requestAnimationFrame(update);
  }
  init()
})